import { useState, useRef, useEffect } from 'react'
import { IBook } from './interface/book'

const booksTest: Array<IBook> = [
  {
    author: ' Джерома Сэлинджер',
    name: 'Над пропастью во ржи',
  },
  {
    author: 'Герберт Уелс',
    name: 'Человек-невидимка',
  },
  {
    author: 'Сти́вен Уи́льям Хо́кинг',
    name: 'Мир в ореховой скорлупке',
  },
  {
    author: 'Макс Фрай',
    name: 'Лабиринт Мёнина',
    img: 'data:image/jpeg;base64,',
  },
]
localStorage.setItem('books', JSON.stringify(booksTest))

class BooksLocalStorage {
  constructor() {
    console.log(localStorage.getItem('books'))
    if (localStorage.getItem('books') === null) {
      localStorage.setItem('books', JSON.stringify(booksTest))
    }
  }

  setAll(books: Array<IBook>) {
    localStorage.setItem('books', JSON.stringify(books))
  }
  set(book: IBook) {
    const books = this.getAll()
    books.push(book)
    this.setAll(books)
  }
  getAll() {
    const localBooks = localStorage.getItem('books')
    const localBooksStr: string =
      typeof localBooks === 'object' ? '[]' : localBooks
    const books: Array<IBook> = JSON.parse(localBooksStr)
    return books
  }
  delete(book: IBook) {
    const books = this.getAll()
    this.setAll(
      books.filter(
        (el) =>
          !el.author.toLowerCase().includes(book.author.toLowerCase()) ||
          !el.name.toLowerCase().includes(book.name.toLowerCase())
      )
    )
  }
  edit(book: IBook, nextBook: IBook) {
    const books = this.getAll()

    const selectBook = books.find(
      (el) => el.author === book.author && el.name === book.name
    )
    const othersBooks = books.filter(
      (el) => !(el.author === book.author && el.name === book.name)
    )
    if (selectBook) {
      selectBook.author = nextBook.author
      selectBook.name = nextBook.name
      if (nextBook.img) {
        selectBook.img = nextBook.img
      }
    }

    this.setAll(books)
  }
}
const bookInstance = new BooksLocalStorage()

const useStateBooks = () => {
  useEffect(() => {
    bookInstance.delete.bind(bookInstance)
    bookInstance.set.bind(bookInstance)
    bookInstance.setAll.bind(bookInstance)
    bookInstance.edit.bind(bookInstance)
  }, [])

  const [books, setBooks] = useState(bookInstance.getAll)

  return {
    books: books,
    set: (book: IBook) => {
      bookInstance.set(book)
      setBooks(bookInstance.getAll())
    },
    delete: (book: IBook) => {
      bookInstance.delete(book)
      setBooks(bookInstance.getAll())
    },
    setAll: (books: Array<IBook>) => {
      bookInstance.setAll(books)
      setBooks(bookInstance.getAll())
    },
    edit: (book: IBook, nextBook: IBook) => {
      bookInstance.edit(book, nextBook)
      setBooks(bookInstance.getAll())
    },
  }
}
export default useStateBooks
